package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.controllers;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.AdmissionRecord;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.services.AdmissionRecordService;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.Optional;@RestController@RequestMapping(path = "/admissionRecord")public class AdmissionRecordController {    private final AdmissionRecordService admissionRecordService;    public AdmissionRecordController(AdmissionRecordService admissionRecordService)    {        this.admissionRecordService = admissionRecordService;    }    @PostMapping    public ResponseEntity<AdmissionRecord> createNewAdmission(@RequestBody AdmissionRecord admissionRecord)    {        AdmissionRecord newAdmission = admissionRecordService.createNewAdmission(admissionRecord);        return  new ResponseEntity<>(newAdmission, HttpStatus.CREATED);    }    @GetMapping(path = "/{admissionId}")    public ResponseEntity<AdmissionRecord> getAdmissionById(@PathVariable Long admissionId)    {        Optional<AdmissionRecord> RequiredAdmissionRecord = admissionRecordService.getAdmissionById(admissionId);        return RequiredAdmissionRecord.map(admissionRecord -> ResponseEntity.ok(admissionRecord)).orElse(ResponseEntity.notFound().build());    }    @PutMapping(path = "/{studentId}/admission/{admissionId}")    public ResponseEntity<AdmissionRecord> takeAdmission(@PathVariable Long studentId, @PathVariable Long admissionId)    {        return admissionRecordService.takeAdmission(studentId,admissionId);    }}