package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.controllers;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.AdmissionRecord;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.services.AdmissionRecordService;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping(path = "/admissionRecord")public class AdmissionRecordController {    private final AdmissionRecordService admissionRecordService;    public AdmissionRecordController(AdmissionRecordService admissionRecordService)    {        this.admissionRecordService = admissionRecordService;    }    @PostMapping    public AdmissionRecord createNewAdmission(@RequestBody AdmissionRecord admissionRecord)    {        return admissionRecordService.createNewAdmission(admissionRecord);    }    @GetMapping(path = "/{admissionId}")    public AdmissionRecord getAdmissionById(@PathVariable Long admissionId)    {        return admissionRecordService.getAdmissionById(admissionId);    }    @PutMapping(path = "/{studentId}/admission/{admissionId}")    public AdmissionRecord takeAdmission(@PathVariable Long studentId, @PathVariable Long admissionId)    {        return admissionRecordService.takeAdmission(studentId,admissionId);    }}