package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.controllers;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.Professor;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.services.ProfessorService;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping("/professor")public class ProfessorController {    private final ProfessorService professorService;    public ProfessorController(ProfessorService professorService)    {        this.professorService = professorService;    }    @PostMapping    public Professor createNewProfessor(@RequestBody Professor professor)    {        return professorService.createNewProfessor(professor);    }    @GetMapping(path = "/{professorId}")    public Professor getProfessorById(@PathVariable Long professorId)    {        return professorService.getProfessorById(professorId);    }    @PutMapping(path = "/{subjectId}/assignSubject/{professorId}")    public Professor assignSubjectToProfessor(@PathVariable Long subjectId, @PathVariable Long professorId)    {        return professorService.assignSubjectToProfessor(subjectId,professorId);    }    @PutMapping("/{professorId}/students/{studentId}")    public Professor assignStudentToProfessor(@PathVariable Long studentId, @PathVariable Long professorId) {        return professorService.assignStudentToProfessor(studentId, professorId);    }}