package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.controllers;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.Student;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.services.StudentService;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping(path = "/students")public class StudentController {    private StudentService studentService;    public StudentController(StudentService studentService)    {        this.studentService = studentService;    }    @PostMapping    public Student createNewStudent(@RequestBody Student student)    {        return studentService.createNewStudent(student);    }    @GetMapping(path = "/{studentId}")    public Student getStudentById(@PathVariable Long studentId)    {        return studentService.getStudentById(studentId);    }    @PutMapping(path = "/{studentId}/Subject/{subjectId}")    public Student assignSubjectToStudent(@PathVariable Long studentId , @PathVariable Long subjectId)    {        return studentService.assignSubjectToStudent(studentId,subjectId);    }}