package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.controllers;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.Student;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.services.StudentService;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.Optional;@RestController@RequestMapping(path = "/students")public class StudentController {    private StudentService studentService;    public StudentController(StudentService studentService)    {        this.studentService = studentService;    }    @PostMapping    public ResponseEntity<Student> createNewStudent(@RequestBody Student student)    {        Student newStudent = studentService.createNewStudent(student);        return  new ResponseEntity<>(newStudent, HttpStatus.CREATED);    }    @GetMapping(path = "/{studentId}")    public ResponseEntity<Student> getStudentById(@PathVariable Long studentId)    {        Optional<Student>student =  studentService.getStudentById(studentId);        return student.map(student1 -> ResponseEntity.ok(student1)).orElse(ResponseEntity.notFound().build());    }    @PutMapping(path = "/{studentId}/Subject/{subjectId}")    public ResponseEntity<Student> assignSubjectToStudent(@PathVariable Long studentId , @PathVariable Long subjectId)    {        return studentService.assignSubjectToStudent(studentId,subjectId);    }}