package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.controllers;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.AdmissionRecord;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.Subject;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.services.SubjectService;import org.springframework.web.bind.annotation.*;@RestController@RequestMapping(path = "/subjects")public class SubjectController {   private final SubjectService subjectService;   public SubjectController(SubjectService subjectService)   {       this.subjectService = subjectService;   }    @PostMapping    public Subject createNewSubject(@RequestBody Subject subject)    {        return subjectService. createNewSubject(subject);    }    @GetMapping(path = "/{subjectId}")    public Subject getSubjectById(@PathVariable Long subjectId)    {        return subjectService.getSubjectById(subjectId);    }}