package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.controllers;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.AdmissionRecord;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.Subject;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.services.SubjectService;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.Optional;@RestController@RequestMapping(path = "/subjects")public class SubjectController {   private final SubjectService subjectService;   public SubjectController(SubjectService subjectService)   {       this.subjectService = subjectService;   }    @PostMapping    public ResponseEntity<Subject> createNewSubject(@RequestBody Subject subject)    {        Subject newSubject = subjectService. createNewSubject(subject);        return  new ResponseEntity<>(newSubject, HttpStatus.CREATED);    }    @GetMapping(path = "/{subjectId}")    public ResponseEntity<Subject> getSubjectById(@PathVariable Long subjectId)    {        Optional<Subject> RequiredSubject =  subjectService.getSubjectById(subjectId);        return RequiredSubject.map(subject -> ResponseEntity.ok(subject)).orElse(ResponseEntity.notFound().build());    }}