package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.Objects;import java.util.Set;@NoArgsConstructor@AllArgsConstructor@Getter@Setter@Entitypublic class Professor {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long professor_id;    private String professor_name;    @OneToMany(mappedBy = "professor")    @JsonIgnore    private Set<Subject> subjects;    @ManyToMany(cascade ={CascadeType.PERSIST,CascadeType.MERGE} )    @JoinTable(            name = "professor_student",            joinColumns = @JoinColumn(name = "professor_id"),            inverseJoinColumns = @JoinColumn(name = "student_id")    )    private Set<Student> students;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Professor professor = (Professor) o;        return Objects.equals(professor_id, professor.professor_id) && Objects.equals(professor_name, professor.professor_name);    }    @Override    public int hashCode() {        return Objects.hash(professor_id, professor_name);    }}