package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.util.Objects;import java.util.Set;@NoArgsConstructor@AllArgsConstructor@Getter@Setter@Entitypublic class Student {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long student_id;    private String name;    @OneToOne(mappedBy = "student")    @JsonIgnore    private AdmissionRecord admissionRecord;    @ManyToMany(mappedBy = "students")    @JsonIgnore    private Set<Professor> professors;    @ManyToMany(cascade = {CascadeType.PERSIST , CascadeType.MERGE})    @JoinTable(name = "Students_Subjects",    joinColumns = @JoinColumn(name = "student_id"),    inverseJoinColumns = @JoinColumn(name = "Subject_id"))    private Set<Subject> subjects;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Student student = (Student) o;        return Objects.equals(student_id, student.student_id) && Objects.equals(name, student.name);    }    @Override    public int hashCode() {        return Objects.hash(student_id, name);    }}