package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import org.hibernate.annotations.Cascade;import java.util.Objects;import java.util.Set;@NoArgsConstructor@AllArgsConstructor@Getter@Setter@Entitypublic class Subject {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long Subject_id;    private  String subject_name;    @ManyToOne(cascade = CascadeType.ALL)    @JoinColumn(name="professor_id")    private Professor professor;    @ManyToMany(mappedBy = "subjects")    @JsonIgnore    private Set<Student> student;    @Override    public boolean equals(Object o) {        if (this == o) return true;        if (o == null || getClass() != o.getClass()) return false;        Subject subject = (Subject) o;        return Objects.equals(Subject_id, subject.Subject_id) && Objects.equals(subject_name, subject.subject_name);    }    @Override    public int hashCode() {        return Objects.hash(Subject_id, subject_name);    }}