package com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.services;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.Professor;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.entites.Subject;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.repositories.ProfessorRepo;import com.codingshuttle.tutorial.dataMapping.CollegeManagmentSystem.repositories.SubjectRepo;import org.springframework.stereotype.Service;import javax.swing.text.html.Option;import java.util.Optional;@Servicepublic class SubjectService {    private final SubjectRepo subjectRepo;    public SubjectService(SubjectRepo subjectRepo)    {        this.subjectRepo=subjectRepo;    }    public Subject createNewSubject(Subject subject) {        return subjectRepo.save(subject);    }    public Optional<Subject> getSubjectById(Long subjectId) {        return subjectRepo.findById(subjectId);    }}